import { Theme } from "@mui/material";
import { makeStyles } from "@mui/styles";
const productsStyle   = makeStyles((theme:Theme) => ({
  header: {
    
      
    overflow: "hidden",
    backgroundColor: "#f1f1f1",
    [theme.breakpoints.up(1500)]: {
      backgroundColor: "red",
    },
    [theme.breakpoints.between(1200,1500)]: {
      backgroundColor: "blue",
    },
    [theme.breakpoints.down(1200)]: {
      backgroundColor: "white",
    },
  },

  headerP :{
    float: "left",
    color: "black",
    textAlign: "center",
    padding: "10px",
    textDecoration: "none",
    fontDize: "18px",
    lineHeight: "15px",
    borderRadius: "4px"
},

headerPlogo :{
  fontSize: "25px",
  fontWeight: "bold",
  color: "#5c5239"
},

headerPhover :{
  backgroundColor: "#ddd",
  color: "#5d6770"
},

headerPactive :{
  backgroundColor: "#917c49",
  color: "white"
},

headerRight: {
  float: "right"
}

/*@media screen and (maxWidth: "500px") :{
  headerP :{
      float: "none" ,
      display: "block",
      textAlign: "left"
  },
  headerRight :{
      float: "none"
  }

*/ 
})); 

export default productsStyle;
