import { Component, useEffect, useState } from "react";
import axios from "axios";
import { useNavigate, useLocation, useParams } from "react-router-dom";
import productsStyle from "../styles/product.style";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import { setConstantValue } from "typescript";
import { Alert, Snackbar } from "@mui/material";
//import { useStore } from "../useStore/useStoreProduct";
import { Product } from "../interfaces/product";
import { useSelector, useDispatch } from "react-redux";
import { useFormik, Formik } from 'formik';
import * as yup from 'yup';
import {
  RootState,
  setOpenAlert,
  updateProducts,
  addProducts,
  AppDispatch,
  getProducts,
} from "../store/productStore";

const validationSchema = yup.object({
   title: yup
    .string()
    //.email('Enter a valid email')
    .required('Title is required'),
  price: yup
    .number()
    .min(2, 'Price should be of minimum 2 characters length')
    .required('Price is required'),
  description: yup
    .string()
    //.email('Enter a valid email')
    .required('Description is required'),
  sizeId: yup
    .number()
    //.min(2, 'Password should be of minimum 2 characters length')
    .required('Size is required'),
});

const AddEdit: React.FC = () => {

  let products = useSelector(
    (state: RootState) => state.product.listProducts
  );

  const dispatch: AppDispatch = useDispatch();

  const navigate = useNavigate();
  let { id } = useParams();
  let idProductToEdit = parseInt(id ? id : "0");
  console.log("idProductToEdit",idProductToEdit)

  useEffect(() => {
    console.log("useeeffect", idProductToEdit);
    dispatch(getProducts()).then((data) => {
      if (idProductToEdit&&idProductToEdit!==0) {
        let productToUpdate = data.payload.find(
          /* listeProduct*/
          (element: Product) => element.id == idProductToEdit
        );
        if (productToUpdate) {
          setProductsData(productToUpdate);
        }      }
        else{
          console.log("else add")
          setProductsData({
            id: 0,
            title: "",
            price: 0,
            description: "",
            published: 0,
            sizeId: 0,
          })
        }
    });
  }, [idProductToEdit]);

  const getSingleProduct = async (id: number) => {
    let productToUpdate = products.find(
      /* listeProduct*/
      (element: Product) => element.id == id
    );
    console.log("productToUpdate", products, productToUpdate, id);
    if (productToUpdate) {
      setProductsData(productToUpdate);
    }
  };

  const [ProductsData, setProductsData] = useState<Product>({
    id: 0,
    title: "",
    price: 0,
    description: "",
    published: 0,
    sizeId: 0,
  });
  //const [message, setMessage] = useState("");
  const [errorr, setError] = useState(false);
  //const [openAlert, setOpenAlert] = useState(false);

  //const { title, price, description, published, sizeId } = Product;

  /*const addProduct1 = async (data: Product) => {
    const response = await axios.post(
      "http://localhost:3001/api/products/addProduct",
      data
    );
    if (response.status == 200) {
      dispatch(addProduct(response.data.data));

      setMessage("Success");
    } else {
      setError(true);
      setMessage("Error");
    }
    setOpenAlert(true);
  };*/

  /*const updateProduct = async (id: number) => {
    console.log("update",id)
    const response = await axios.put(
      `http://localhost:3001/api/products/${id}`,ProductsData
    );
    if (response.status == 200) {
      dispatch(updateProduct2 ({id, ProductsData}) ); 
      setMessage("Success");
    } else {
      setError(true);
      setMessage("Error");
    }
    setOpenAlert(true);
  };  */

  const handleSubmit = (e: any) => {
    e.preventDefault();
    if (
      !ProductsData.title ||
      !ProductsData.price ||
      !ProductsData.description ||
      !ProductsData.sizeId
    ) {
     /* setError(true);
      setMessage("Please provide value into each input failed");*/
    } else {
      if (idProductToEdit==0) {
        //addProduct1(ProductsData);
        dispatch(addProducts(ProductsData));
      } else {
        dispatch(updateProducts({ idProductToEdit, ProductsData }));
      }
      navigate("/");
    }
    //navigate("/");
   // setOpenAlert(true);
  // formik.handleSubmit()
  };

  const handleInputChange = (e: any) => {
    let { name, value } = e.target;
    if (value) {
     setError(false);
    } else {
      setError(true);
    }
    setProductsData({ ...ProductsData, [name]: value });
    console.log("product", ProductsData);
  };
  

  let {error,openAlert,message} = useSelector(
    (state: RootState) => state.product
  );
  const handleCloseAlert = () => {
    dispatch(setOpenAlert(false));
  };

  const classes = productsStyle();

  
    const formik = useFormik({
      initialValues: {title: "",
      price: 0,
      description: "",
      sizeId: 0}, 
      validationSchema: validationSchema,
      onSubmit: (values) => {
       alert(JSON.stringify(values, null, 2));
        //console.log(values)
      },
      
    });

  return (
    <div>
       
     <Snackbar
        open={openAlert}
        autoHideDuration={6000}
        onClose={handleCloseAlert}
       >
        <Alert
          onClose={handleCloseAlert}
          severity="success"
          sx={{ width: "100%" }}
        >
          {message}
        </Alert>
      </Snackbar  > 
      <Formik
       initialValues={{ProductsData
      }}
      validationSchema={validationSchema}
      onSubmit={values => {
        // same shape as initial values
        console.log(values);
      }}
      
      >
      <Box
        onSubmit={handleSubmit}
        component="form"
        sx={{
          "& > :not(style)": { m: 1, width: "45ch" },
        }}
        noValidate
        autoComplete="off"
        textAlign={"center"}
        display="contents"
        //justifyItems={"center"}
      >
        <TextField
          fullWidth 
          type="text"
          id="standard-basic"
          label="Title"
          name="title"
          variant="standard"
          onChange={handleInputChange}
          value={ProductsData.title}
          error={formik.touched.title && Boolean(formik.errors.title)}
          //error={error}
         // helperText={error ? "title is required" : ""}
          helperText={formik.touched.title && formik.errors.title}
        />

        <br />
        <TextField
          fullWidth
          type="number"
          id="standard-basic"
          label="Price"
          name="price"
          variant="standard"
          onChange={handleInputChange}
          value={ProductsData.price}
          error={formik.touched.price && Boolean(formik.errors.price)}
          helperText={formik.touched.price && formik.errors.price}
          
        />
        <br />
        <TextField 
          fullWidth
          type="text"
          id="standard-basic"
          label="Description"
          name="description"
          variant="standard"
          onChange={handleInputChange}
          value={ProductsData.description}
          error={formik.touched.description && Boolean(formik.errors.description)}
          helperText={formik.touched.description && formik.errors.description}
        />
        <br />
        <TextField 
          fullWidth
          type="text"
          id="standard-basic"
          label="Published"
          name="published"
          variant="standard"
          onChange={handleInputChange}
          value={ProductsData.published}
        />
        <br />
        <TextField
          fullWidth
          type="number"
          id="standard-basic"
          label="Size"
          name="sizeId"
          variant="standard"
          onChange={handleInputChange}
          value={ProductsData.sizeId}
          error={formik.touched.sizeId && Boolean(formik.errors.sizeId)}
          helperText={formik.touched.sizeId && formik.errors.sizeId}
        />
        <br />
        <div className={classes.buttonStyle}>
          <Button
            style={{
              width: "100px",
              //textAlign: "center",
              display: "block",
              margin: "0 auto",
            }}
            value="add"
            type="submit"
            variant="contained"
            aria-label="contained primary button group"
            color="primary"
          >
            {idProductToEdit ? "Update" : "Add"}
          </Button>
        </div>
      </Box>
      </Formik>
    </div>
  );

};
export default AddEdit;
