import productsServices from '../services/products.services';
import {Product} from '../interfaces/product'
import {createSlice, PayloadAction } from '@reduxjs/toolkit'
import { configureStore } from '@reduxjs/toolkit'

let  listProducts :Array<Product> = [];

const  productSlice = createSlice({

  name: 'product',
  initialState: listProducts ,
  reducers: {
    setListProducts(state, action:PayloadAction<Array<Product>>){
      listProducts=[...action.payload]
      },
    
    addProduct(state, action:PayloadAction<Product>){
        listProducts.push(action.payload)
      },
    
    onDeleteProduct(state, action:PayloadAction<number>){
        let newList=[...listProducts]
        let index= newList.findIndex((element:Product)=>element.id===action.payload)
        newList.splice(index,1)
        listProducts=newList
      },
    
    updateProduct(state, action:PayloadAction<{id:number,newProduct:Product}> ) {
        let newList=[...listProducts]
        let index =newList.findIndex((element:Product)=>element.id===action.payload.id)
        
        newList[index]=action.payload.newProduct
        listProducts=newList
    
      },

    getProducts (state) {
        productsServices.getProduct().then((response:any)=>{
          if (response.status== 200){
            console.log("data",response.data.data)
            listProducts=(response.data.data)
           }
        })
         } ,
    
  },
});


export const store  = configureStore({
  reducer : {
    product : productSlice.reducer
  }
})

export const createToggle = (listProducts: Array<Product>) =>{
  return {
    type : "product/setListProducts",
    payload : listProducts
  }
}

export const addProduct = (product: Product) =>{
  return {
    type : "product/addProduct",
    payload : product
  }
} 
export const onDeleteProduct = (id:number) =>{
  return {
    type : "product/onDeleteProduct",
    payload : id
  }
} 
export const updateProduct = (id:number, newProduct: Product) =>{
  return {
    type : "product/updateProduct",
    payload : id,newProduct
    
  }
} 
export const getProducts = () =>{
  return {
    type : "product/getProducts",
  }
}
