import { makeAutoObservable } from "mobx";
export interface Product {
  id: number;
  title: string;
  price: number;
  description: string;
  published: number;
  sizeId: number;
}

const productStore = () => {
  return makeAutoObservable({
    array: [] as Product[],
  });
};


export default productStore;
