import { Component, useEffect, useState } from "react";
import axios from "axios";
import {useNavigate, useLocation} from 'react-router-dom';
import productsStyle from "../styles/product.style";
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Button from "@mui/material/Button";
import { setConstantValue } from "typescript";
import { Alert, Snackbar } from "@mui/material";

enum  Product  {
    title= "" ,
    price= "",
    description= "",
    published= "",
    sizeId= "",
}



const AddEdit: React.FC = () => {
//const [ProductsData, setProductsData] = useState<Array<Product>>([]);
const [ProductsData, setProductsData] = useState(Product);
const [message, setMessage] = useState("");
const [error, setError] = useState(false);
const [openAlert, setOpenAlert] = useState(false);

const {title, price, description, published, sizeId} =Product;

const addProduct = async (data :any) => {
    const response = await axios.post("http://localhost:3001/api/products/addProduct", data);
    if (response.status== 200){
        setMessage("Success")
       }else{
        setError(true)
        setMessage("Error")
       }
       setOpenAlert(true)
}



const handleSubmit = (e : any) => {
  e.preventDefault();
  addProduct(ProductsData);
 }

const handleInputChange = (e : any) => {
    let { title, value} = e.target;
    
    setProductsData({ ...ProductsData, [title]: value })
}

 const classes=productsStyle()
    return (
        <div style={{marginTop: "50px"}}>
         <Box onSubmit={handleSubmit}
      component="form"
      sx={{
        '& > :not(style)': { m: 1, width: '45ch' },
      }}
      noValidate
      autoComplete="off"
      textAlign={"center"}
    >
      
      <TextField  type ="text" id="standard-basic" label="Title" variant="standard" onChange={handleInputChange} value={title}/> 
      <br/>
      <TextField  type ="text" id="standard-basic" label="Price" variant="standard" onChange={handleInputChange}  value={price}/> 
      <br/>
      <TextField  type ="text" id="standard-basic" label="Description" variant="standard" onChange={handleInputChange}  value={description}/> 
      <br/>
      <TextField  type ="text" id="standard-basic" label="Published" variant="standard" onChange={handleInputChange}  value={published}/> 
      <br/>
      <TextField  type ="text" id="standard-basic" label="Size" variant="standard" onChange={handleInputChange}  value={sizeId}/>
      <br/>
      <div className={classes.buttonStyle}>
      <Button style={{width:"100px", textAlign:"center", display:"block", margin:"0 auto"}}  value="add" type="submit" variant="contained" aria-label="contained primary button group" color="primary">Submit</Button>
      </div>
    </Box>

    <Snackbar open={open} autoHideDuration={6000} onClose={handleCloseAlert}
> <Alert onClose={handleCloseAlert} severity="success" sx={{ width: '100%' }}>
    {message}
  </Alert></Snackbar>
        </div>
    )

}
export default AddEdit;
