export interface Product {
  id: number;
  title: string;
  price: number;
  description: string;
  published: number;
  sizeId: number;
}

import { makeObservable, observable, computed, action } from 'mobx';

class productStore {
  @observable count = 0;

  constructor() {
    makeObservable(this);
  }

  get isNegative() {
    return this.count < 0 ? 'Yes' : 'No';
  }

  increase() {
    this.count += 1;
  }

  decrease() {
    this.count -= 1;
  }
}
export default productStore;
