import { Component, useEffect, useState } from "react";
import axios from "axios";
import { useNavigate, useLocation, useParams } from "react-router-dom";
import productsStyle from "../styles/product.style";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import { setConstantValue } from "typescript";
import { Alert, Snackbar } from "@mui/material";
import { useStore } from "../useStore/useStoreProduct";
import { observer } from "mobx-react-lite";
import { Product } from "../store/productStore";

const AddEdit: React.FC = observer(() => {
  const navigate = useNavigate();
  const { id } = useParams();
  useEffect(() => {
    if (id) {
      getSingleProduct(id);
    }
  }, [id]);

  const getSingleProduct = async (id: string) => {
    /* const response = await axios.get(`http://localhost:3001/api/products/${id}`);
    if (response.status ==200){
     // productStore.onOneProduct(response.data.data)
    setProductsData({...response.data.data})
  }*/
    let productToUpdate = productStore.listProducts.find(
      (element: Product) => element.id == id
    );
    console.log("productToUpdate", productToUpdate, id);
    if (productToUpdate) {
      setProductsData(productToUpdate);
    }
  };

  const { productStore } = useStore();
  //const [ProductsData, setProductsData] = useState<Array<Product>>([]);
  const [ProductsData, setProductsData] = useState<Product>({
    id: 0,
    title: "",
    price: 0,
    description: "",
    published: 0,
    sizeId: 0,
  });
  const [message, setMessage] = useState("");
  const [error, setError] = useState(false);
  const [openAlert, setOpenAlert] = useState(false);

  //const { title, price, description, published, sizeId } = Product;

  const addProduct = async (data: any) => {
    const response = await axios.post(
      "http://localhost:3001/api/products/addProduct",
      data
    );
    if (response.status == 200) {
      /*let newList=[...productStore.listProducts]
      newList.push(response.data.data)
      productStore.setListProducts(newList)*/

      productStore.addProduct(response.data.data);

      setMessage("Success");
    } else {
      setError(true);
      setMessage("Error");
    }
    setOpenAlert(true);
  };

  const updateProduct = async (id: any) => {
    console.log("update",id)
    const response = await axios.put(
      `http://localhost:3001/api/products/${id}`,ProductsData
    );
    if (response.status == 200) {
      productStore.updateProduct(id,ProductsData);

      setMessage("Success");
    } else {
      setError(true);
      setMessage("Error");
    }
    setOpenAlert(true);
  };

  const handleSubmit = (e: any) => {
    e.preventDefault();
    if (
      !ProductsData.title ||
      !ProductsData.price ||
      !ProductsData.description ||
      !ProductsData.sizeId
    ) {
      setError(true);
      setMessage("Please provide value into each input failed");
    } else {
      if (!id) {
        addProduct(ProductsData);
      } else {
        updateProduct(id);
      }
    }
    navigate("/");
    setOpenAlert(true);
  };

  const handleInputChange = (e: any) => {
    let { name, value } = e.target;
    if (value) {
      setError(false);
    } else {
      setError(true);
    }
    console.log("title", name);
    setProductsData({ ...ProductsData, [name]: value });
    console.log("product", ProductsData);
  };

  const handleCloseAlert = () => {
    setOpenAlert(false);
  };

  const classes = productsStyle();
  return (
    <div>
      <Snackbar
        open={openAlert}
        autoHideDuration={6000}
        onClose={handleCloseAlert}
      >
        <Alert
          onClose={handleCloseAlert}
          severity="success"
          sx={{ width: "100%" }}
        >
          {message}
        </Alert>
      </Snackbar>
      <Box
        onSubmit={handleSubmit}
        component="form"
        sx={{
          "& > :not(style)": { m: 1, width: "45ch" },
        }}
        noValidate
        autoComplete="off"
        textAlign={"center"}
        display="contents"
        //justifyItems={"center"}
      >
        <TextField
          type="text"
          id="standard-basic"
          label="Title"
          name="title"
          variant="standard"
          onChange={handleInputChange}
          value={ProductsData.title}
          error={error}
          helperText={error ? "title is required" : ""}
        />

        <br />
        <TextField
          type="text"
          id="standard-basic"
          label="Price"
          name="price"
          variant="standard"
          onChange={handleInputChange}
          value={ProductsData.price}
        />
        <br />
        <TextField
          type="text"
          id="standard-basic"
          label="Description"
          name="description"
          variant="standard"
          onChange={handleInputChange}
          value={ProductsData.description}
        />
        <br />
        <TextField
          type="text"
          id="standard-basic"
          label="Published"
          name="published"
          variant="standard"
          onChange={handleInputChange}
          value={ProductsData.published}
        />
        <br />
        <TextField
          type="text"
          id="standard-basic"
          label="Size"
          name="sizeId"
          variant="standard"
          onChange={handleInputChange}
          value={ProductsData.sizeId}
        />
        <br />
        <div className={classes.buttonStyle}>
          <Button
            style={{
              width: "100px",
              //textAlign: "center",
              display: "block",
              margin: "0 auto",
            }}
            value="add"
            type="submit"
            variant="contained"
            aria-label="contained primary button group"
            color="primary"
          >
            {id ? "Update" : "Add"}
          </Button>
        </div>
      </Box>
    </div>
  );
});
export default AddEdit;
