import { Theme } from "@mui/material";
import { makeStyles } from "@mui/styles";
const productsStyle   = makeStyles((theme:Theme) => ({
  header: {
    
    overflow: "hidden",
    backgroundColor: "#f1f1f1"
  },
  stepper: {
    width: "100%",
    display: "flex",
    flexDirection: "column",
  },
  contentStepperOne: {
    marginTop: "11vw",
    marginLeft: "14vw",
    marginRight: "14vw",
  },
  buttonStepper: {
    marginRight: "14vw",
    marginTop: "8vw",
  },
}));

export default productsStyle;
