import { makeObservable, observable, computed, action } from 'mobx';
import productsServices from '../services/products.services';
export interface Product {
  id: number;
  title: string;
  price: number;
  description: string;
  published: number;
  sizeId: number;
}

class productStore {
  @observable listProducts :Array<Product> = [];

  constructor() {
    makeObservable(this);
  }
  
  @action setListProducts(products:Array<Product>){
  this.listProducts=[...products]
  }
  @action addProduct(product:Product){
    this.listProducts.push(product)
  }
  @action onDeleteProduct(idProduct:number){
    let newList=[...this.listProducts]
    let index= newList.findIndex((element:Product)=>element.id===idProduct)
    console.log("index",index)
    newList.splice(index,1)
    this.listProducts=newList
  }

  

  @action updateProduct(idProduct:number,newProduct:Product){
    let newList=[...this.listProducts]
    let index =newList.findIndex((element:Product)=>element.id===idProduct)
    newList[index]=newProduct
    this.listProducts=newList

  }

  @action  getProducts = () => {
    productsServices.getProduct().then((response:any)=>{
      if (response.status== 200){
        this.listProducts=response.data.data
       }
    })
     }
}
export default productStore;
