import { Component, useEffect, useState } from "react";
import axios from "axios";
import { useNavigate, useLocation, useParams } from "react-router-dom";
import productsStyle from "../styles/product.style";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import { setConstantValue } from "typescript";
import { Alert, Snackbar } from "@mui/material";
//import { useStore } from "../useStore/useStoreProduct";
import { Product } from "../interfaces/product";
import { useSelector, useDispatch } from "react-redux";
import {
  RootState,
  setListProducts,
  updateProducts,
  addProducts,
  AppDispatch,
  getProducts,
} from "../store/productStore";

const AddEdit: React.FC = () => {
  let products = useSelector(
    (state: RootState) => state.product.listProducts
  );
  console.log("products",products)

  const dispatch: AppDispatch = useDispatch();

  const navigate = useNavigate();
  let { id } = useParams();
  let idProductToEdit = parseInt(id ? id : "0");
  useEffect(() => {
    console.log("useeeffect", idProductToEdit);
    dispatch(getProducts()).then((data) => {
      console.log("then",data.payload)
      if (idProductToEdit) {
        let productToUpdate = products.find(
          /* listeProduct*/
          (element: Product) => element.id == idProductToEdit
        );
        console.log("productToUpdate", products, productToUpdate, id);
        if (productToUpdate) {
          setProductsData(productToUpdate);
        }      }
    });
  }, []);

  const getSingleProduct = async (id: number) => {
    let productToUpdate = products.find(
      /* listeProduct*/
      (element: Product) => element.id == id
    );
    console.log("productToUpdate", products, productToUpdate, id);
    if (productToUpdate) {
      setProductsData(productToUpdate);
    }
  };

  const [ProductsData, setProductsData] = useState<Product>({
    id: 0,
    title: "",
    price: 0,
    description: "",
    published: 0,
    sizeId: 0,
  });
  const [message, setMessage] = useState("");
  const [error, setError] = useState(false);
  const [openAlert, setOpenAlert] = useState(false);

  //const { title, price, description, published, sizeId } = Product;

  /*const addProduct1 = async (data: Product) => {
    const response = await axios.post(
      "http://localhost:3001/api/products/addProduct",
      data
    );
    if (response.status == 200) {
      dispatch(addProduct(response.data.data));

      setMessage("Success");
    } else {
      setError(true);
      setMessage("Error");
    }
    setOpenAlert(true);
  };*/

  /*const updateProduct = async (id: number) => {
    console.log("update",id)
    const response = await axios.put(
      `http://localhost:3001/api/products/${id}`,ProductsData
    );
    if (response.status == 200) {
      dispatch(updateProduct2 ({id, ProductsData}) ); 
      setMessage("Success");
    } else {
      setError(true);
      setMessage("Error");
    }
    setOpenAlert(true);
  };  */

  const handleSubmit = (e: any) => {
    e.preventDefault();
    if (
      !ProductsData.title ||
      !ProductsData.price ||
      !ProductsData.description ||
      !ProductsData.sizeId
    ) {
      setError(true);
      setMessage("Please provide value into each input failed");
    } else {
      if (!idProductToEdit) {
        //addProduct1(ProductsData);
        dispatch(addProducts(ProductsData));
      } else {
        console.log("idProductToEdit", idProductToEdit);
        dispatch(updateProducts({ idProductToEdit, ProductsData }));
      }
      navigate("/");
    }
    //navigate("/");
    setOpenAlert(true);
  };

  const handleInputChange = (e: any) => {
    let { name, value } = e.target;
    if (value) {
      setError(false);
    } else {
      setError(true);
    }
    console.log("title", name);
    setProductsData({ ...ProductsData, [name]: value });
    console.log("product", ProductsData);
  };

  const handleCloseAlert = () => {
    setOpenAlert(false);
  };

  const classes = productsStyle();
  return (
    <div>
      <Snackbar
        open={openAlert}
        autoHideDuration={6000}
        onClose={handleCloseAlert}
      >
        <Alert
          onClose={handleCloseAlert}
          severity="success"
          sx={{ width: "100%" }}
        >
          {message}
        </Alert>
      </Snackbar>
      <Box
        onSubmit={handleSubmit}
        component="form"
        sx={{
          "& > :not(style)": { m: 1, width: "45ch" },
        }}
        noValidate
        autoComplete="off"
        textAlign={"center"}
        display="contents"
        //justifyItems={"center"}
      >
        <TextField
          type="text"
          id="standard-basic"
          label="Title"
          name="title"
          variant="standard"
          onChange={handleInputChange}
          value={ProductsData.title}
          error={error}
          helperText={error ? "title is required" : ""}
        />

        <br />
        <TextField
          type="text"
          id="standard-basic"
          label="Price"
          name="price"
          variant="standard"
          onChange={handleInputChange}
          value={ProductsData.price}
        />
        <br />
        <TextField
          type="text"
          id="standard-basic"
          label="Description"
          name="description"
          variant="standard"
          onChange={handleInputChange}
          value={ProductsData.description}
        />
        <br />
        <TextField
          type="text"
          id="standard-basic"
          label="Published"
          name="published"
          variant="standard"
          onChange={handleInputChange}
          value={ProductsData.published}
        />
        <br />
        <TextField
          type="text"
          id="standard-basic"
          label="Size"
          name="sizeId"
          variant="standard"
          onChange={handleInputChange}
          value={ProductsData.sizeId}
        />
        <br />
        <div className={classes.buttonStyle}>
          <Button
            style={{
              width: "100px",
              //textAlign: "center",
              display: "block",
              margin: "0 auto",
            }}
            value="add"
            type="submit"
            variant="contained"
            aria-label="contained primary button group"
            color="primary"
          >
            {idProductToEdit ? "Update" : "Add"}
          </Button>
        </div>
      </Box>
    </div>
  );
};
export default AddEdit;
